import React, { useState } from "react";

export default function Login(){
    
    const [userName, setUserName] = useState("");
    const [password, setPassword] = useState("");

    function handleUserName(event){
        let userName = event.target.value;
        console.log(userName);
        setUserName(userName);
    }

    function handlePassword(event){
        let password = event.target.value;
        console.log(password);
        setPassword(password);
    }


    function authLogin(event){
        event.preventDefault();
        // const userName = e.target.elements.userName.value;
        console.log("OLÁÁÁÁÁÁÁÁ")
        localStorage.setItem("userName", userName);
        localStorage.setItem("userPassword", password);
        
    }


    return (
        <div className="login">
            <form>

                <label>Nome</label>
                <input type="text" name="userName" id="userName" onChange={handleUserName}/>
                <label>Senha</label>
                <input type="password" name="password" id="password" onChange={handlePassword}/>
                <button onClick={authLogin}>Entrar</button>

            </form>
        </div>
    )
}
